import React, { useState } from 'react';

const BookingModal = ({ property, isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    checkIn: '',
    checkOut: '',
    guests: 1,
    paymentMethod: 'credit-card',
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    nameOnCard: '',
    email: '',
    phone: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Basic validation
    if (!formData.checkIn || !formData.checkOut) {
      alert('Please select check-in and check-out dates');
      return;
    }
    
    const checkInDate = new Date(formData.checkIn);
    const checkOutDate = new Date(formData.checkOut);
    
    if (checkOutDate <= checkInDate) {
      alert('Check-out date must be after check-in date');
      return;
    }
    
    if (formData.paymentMethod !== 'paypal') {
      if (!formData.cardNumber || !formData.expiryDate || !formData.cvv || !formData.nameOnCard) {
        alert('Please fill in all payment details');
        return;
      }
    }
    
    if (!formData.email || !formData.phone) {
      alert('Please fill in your contact information');
      return;
    }
    
    setLoading(true);

    // Calculate total amount (simplified calculation)
    const nights = Math.ceil((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24));
    const totalAmount = nights * property.price * formData.guests;

    // Simulate booking process
    setTimeout(() => {
      alert(`ðŸŽ‰ Booking confirmed for ${property.title}!\n\nðŸ“… Check-in: ${formData.checkIn}\nðŸ“… Check-out: ${formData.checkOut}\nðŸ‘¥ Guests: ${formData.guests}\nðŸ’° Total: $${totalAmount}\nðŸ’³ Payment: ${formData.paymentMethod}\nðŸ“§ Confirmation sent to: ${formData.email}`);
      setLoading(false);
      onClose();
    }, 2000);
  };

  if (!isOpen) return null;

  const checkInDate = new Date(formData.checkIn);
  const checkOutDate = new Date(formData.checkOut);
  const nights = formData.checkIn && formData.checkOut ? 
    Math.ceil((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24)) : 0;
  const totalAmount = nights * property.price * formData.guests;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="booking-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Book {property.title}</h2>
          <button className="close-button" onClick={onClose}>Ã—</button>
        </div>

        <div className="modal-content">
          <div className="property-summary">
            <img src={property.image} alt={property.title} className="property-thumbnail" />
            <div className="property-details">
              <h3>{property.title}</h3>
              <p>{property.location}</p>
              <p className="price">${property.price}/night</p>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-section">
              <h3>Booking Details</h3>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="checkIn">Check-in Date</label>
                  <input
                    type="date"
                    id="checkIn"
                    name="checkIn"
                    value={formData.checkIn}
                    onChange={handleChange}
                    required
                    min={new Date().toISOString().split('T')[0]}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="checkOut">Check-out Date</label>
                  <input
                    type="date"
                    id="checkOut"
                    name="checkOut"
                    value={formData.checkOut}
                    onChange={handleChange}
                    required
                    min={formData.checkIn || new Date().toISOString().split('T')[0]}
                  />
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="guests">Number of Guests</label>
                <select
                  id="guests"
                  name="guests"
                  value={formData.guests}
                  onChange={handleChange}
                  required
                >
                  <option value={1}>1 Guest</option>
                  <option value={2}>2 Guests</option>
                  <option value={3}>3 Guests</option>
                  <option value={4}>4 Guests</option>
                  <option value={5}>5 Guests</option>
                  <option value={6}>6+ Guests</option>
                </select>
              </div>
            </div>

            <div className="form-section">
              <h3>Payment Method</h3>
              
              <div className="payment-methods">
                <label className="payment-option">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="credit-card"
                    checked={formData.paymentMethod === 'credit-card'}
                    onChange={handleChange}
                  />
                  <span className="payment-label">Credit Card</span>
                </label>
                <label className="payment-option">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="debit-card"
                    checked={formData.paymentMethod === 'debit-card'}
                    onChange={handleChange}
                  />
                  <span className="payment-label">Debit Card</span>
                </label>
                <label className="payment-option">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="paypal"
                    checked={formData.paymentMethod === 'paypal'}
                    onChange={handleChange}
                  />
                  <span className="payment-label">PayPal</span>
                </label>
              </div>

              {formData.paymentMethod !== 'paypal' && (
                <>
                  <div className="form-group">
                    <label htmlFor="cardNumber">Card Number</label>
                    <input
                      type="text"
                      id="cardNumber"
                      name="cardNumber"
                      value={formData.cardNumber}
                      onChange={handleChange}
                      placeholder="1234 5678 9012 3456"
                      required
                    />
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label htmlFor="expiryDate">Expiry Date</label>
                      <input
                        type="text"
                        id="expiryDate"
                        name="expiryDate"
                        value={formData.expiryDate}
                        onChange={handleChange}
                        placeholder="MM/YY"
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="cvv">CVV</label>
                      <input
                        type="text"
                        id="cvv"
                        name="cvv"
                        value={formData.cvv}
                        onChange={handleChange}
                        placeholder="123"
                        required
                      />
                    </div>
                  </div>

                  <div className="form-group">
                    <label htmlFor="nameOnCard">Name on Card</label>
                    <input
                      type="text"
                      id="nameOnCard"
                      name="nameOnCard"
                      value={formData.nameOnCard}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </>
              )}
            </div>

            <div className="form-section">
              <h3>Contact Information</h3>
              
              <div className="form-group">
                <label htmlFor="email">Email Address</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone">Phone Number</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="booking-summary">
              <h3>Booking Summary</h3>
              <div className="summary-item">
                <span>${property.price} Ã— {nights} nights</span>
                <span>${property.price * nights}</span>
              </div>
              <div className="summary-item">
                <span>Guests: {formData.guests}</span>
                <span>+${(property.price * nights * (formData.guests - 1))}</span>
              </div>
              <div className="summary-item total">
                <span>Total Amount</span>
                <span>${totalAmount}</span>
              </div>
            </div>

            <button
              type="submit"
              className="btn btn-primary book-confirm-btn"
              disabled={loading || !formData.checkIn || !formData.checkOut}
            >
              {loading ? 'Processing...' : `Book for $${totalAmount}`}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BookingModal;
