import React, { useMemo, useState } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import { bookingsAPI } from '../services/api';

const BookProperty = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const property = location.state?.property || {};

  const [formData, setFormData] = useState({
    checkIn: '',
    checkOut: '',
    guests: 1,
    name: '',
    email: '',
    phone: '',
    specialRequests: ''
  });
  const [loading, setLoading] = useState(false);

  const nights = useMemo(() => {
    if (!formData.checkIn || !formData.checkOut) return 0;
    const checkInDate = new Date(formData.checkIn);
    const checkOutDate = new Date(formData.checkOut);
    return Math.ceil((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24));
  }, [formData.checkIn, formData.checkOut]);

  const totalAmount = useMemo(() => {
    if (!property?.price || !nights) return 0;
    return nights * property.price * (Number(formData.guests) || 1);
  }, [nights, property, formData.guests]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.checkIn || !formData.checkOut) {
      alert('Please select dates');
      return;
    }
    if (new Date(formData.checkIn) >= new Date(formData.checkOut)) {
      alert('Check-out must be after check-in');
      return;
    }

    try {
      setLoading(true);
      await bookingsAPI.create({
        propertyId: id,
        propertyTitle: property.title,
        propertyLocation: property.location,
        pricePerNight: property.price,
        checkIn: formData.checkIn,
        checkOut: formData.checkOut,
        guests: Number(formData.guests),
        name: formData.name,
        email: formData.email,
        phone: formData.phone,
        specialRequests: formData.specialRequests,
        totalAmount
      });
      alert('Booking confirmed!');
      navigate('/');
    } catch (err) {
      alert(err?.message || 'Failed to create booking');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container" style={{ padding: '24px' }}>
      <h2>Book {property?.title || 'Property'} </h2>
      <p>{property?.location}</p>
      <p>Price: ${property?.price}/night</p>

      <form onSubmit={handleSubmit} className="booking-form-simple">
        <div>
          <label>Check-in</label>
          <input type="date" name="checkIn" value={formData.checkIn} onChange={handleChange} required />
        </div>
        <div>
          <label>Check-out</label>
          <input type="date" name="checkOut" value={formData.checkOut} onChange={handleChange} required />
        </div>
        <div>
          <label>Guests</label>
          <select name="guests" value={formData.guests} onChange={handleChange}>
            {[1,2,3,4,5,6,7,8].map(n => <option key={n} value={n}>{n}</option>)}
          </select>
        </div>
        <div>
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone</label>
          <input type="tel" name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div>
          <label>Special Requests</label>
          <textarea name="specialRequests" rows="3" value={formData.specialRequests} onChange={handleChange} />
        </div>

        <div style={{ marginTop: 12 }}>
          <strong>Total: ${totalAmount}</strong>
        </div>

        <button type="submit" className="btn btn-primary" disabled={loading || totalAmount <= 0}>
          {loading ? 'Booking...' : 'Confirm Booking'}
        </button>
      </form>
    </div>
  );
};

export default BookProperty;
