import React, { useState } from 'react';

const BookingModal = ({ property, isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    checkIn: '',
    checkOut: '',
    guests: 1,
    paymentMethod: 'card',
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    name: '',
    email: '',
    phone: '',
    specialRequests: ''
  });

  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.checkIn || !formData.checkOut) {
      alert('Please select check-in and check-out dates');
      return;
    }

    if (new Date(formData.checkIn) >= new Date(formData.checkOut)) {
      alert('Check-out date must be after check-in date');
      return;
    }

    setLoading(true);

    // Simulate booking process
    setTimeout(() => {
      alert('Booking confirmed! You will receive a confirmation email shortly.');
      setLoading(false);
      onClose();
    }, 2000);
  };

  if (!isOpen) return null;

  const checkInDate = new Date(formData.checkIn);
  const checkOutDate = new Date(formData.checkOut);
  const nights = formData.checkIn && formData.checkOut ? 
    Math.ceil((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24)) : 0;
  const totalAmount = nights * property.price * formData.guests;

  return (
    <div className="fullscreen-booking">
      <div className="booking-header">
        <button className="back-button" onClick={onClose}>
          ← Back to Properties
        </button>
        <h1>Book {property.title}</h1>
      </div>

      <div className="booking-content">
        <div className="property-summary">
          <img src={property.image} alt={property.title} className="property-thumbnail" />
          <div className="property-details">
            <h3>{property.title}</h3>
            <p>{property.location}</p>
            <p className="price">${property.price}/night</p>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-section">
            <h3>Booking Details</h3>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="checkIn">Check-in Date</label>
                <input
                  type="date"
                  id="checkIn"
                  name="checkIn"
                  value={formData.checkIn}
                  onChange={handleChange}
                  min={new Date().toISOString().split('T')[0]}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="checkOut">Check-out Date</label>
                <input
                  type="date"
                  id="checkOut"
                  name="checkOut"
                  value={formData.checkOut}
                  onChange={handleChange}
                  min={formData.checkIn || new Date().toISOString().split('T')[0]}
                  required
                />
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="guests">Number of Guests</label>
              <select
                id="guests"
                name="guests"
                value={formData.guests}
                onChange={handleChange}
              >
                {[1,2,3,4,5,6,7,8].map(num => (
                  <option key={num} value={num}>{num} {num === 1 ? 'guest' : 'guests'}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="form-section">
            <h3>Payment Method</h3>
            <div className="payment-methods">
              <label className="payment-option">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="card"
                  checked={formData.paymentMethod === 'card'}
                  onChange={handleChange}
                />
                <span className="payment-label">Credit/Debit Card</span>
              </label>
              <label className="payment-option">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="paypal"
                  checked={formData.paymentMethod === 'paypal'}
                  onChange={handleChange}
                />
                <span className="payment-label">PayPal</span>
              </label>
            </div>

            {formData.paymentMethod === 'card' && (
              <div className="card-details">
                <div className="form-group">
                  <label htmlFor="cardNumber">Card Number</label>
                  <input
                    type="text"
                    id="cardNumber"
                    name="cardNumber"
                    value={formData.cardNumber}
                    onChange={handleChange}
                    placeholder="1234 5678 9012 3456"
                    maxLength="19"
                  />
                </div>
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="expiryDate">Expiry Date</label>
                    <input
                      type="text"
                      id="expiryDate"
                      name="expiryDate"
                      value={formData.expiryDate}
                      onChange={handleChange}
                      placeholder="MM/YY"
                      maxLength="5"
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="cvv">CVV</label>
                    <input
                      type="text"
                      id="cvv"
                      name="cvv"
                      value={formData.cvv}
                      onChange={handleChange}
                      placeholder="123"
                      maxLength="4"
                    />
                  </div>
                </div>
              </div>
            )}
          </div>

          <div className="form-section">
            <h3>Contact Information</h3>
            <div className="form-group">
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Enter your full name"
                required
              />
            </div>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Enter your email"
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="phone">Phone Number</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="Enter your phone number"
                />
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="specialRequests">Special Requests (Optional)</label>
              <textarea
                id="specialRequests"
                name="specialRequests"
                value={formData.specialRequests}
                onChange={handleChange}
                placeholder="Any special requests or notes..."
                rows="4"
              />
            </div>
          </div>

          <div className="booking-summary">
            <h3>Booking Summary</h3>
            <div className="summary-row">
              <span>${property.price} × {nights} nights × {formData.guests} {formData.guests === 1 ? 'guest' : 'guests'}</span>
              <span>${totalAmount}</span>
            </div>
            <div className="summary-row total">
              <span>Total</span>
              <span>${totalAmount}</span>
            </div>
          </div>

          <button
            type="submit"
            className="btn btn-primary book-confirm-btn"
            disabled={loading || !formData.checkIn || !formData.checkOut}
          >
            {loading ? 'Processing...' : `Book for $${totalAmount}`}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingModal;
